function addBookToTable(book) {
    console.log('کتاب دریافتی:', book);

    // فقط یک بار ردیف را اضافه می‌کنیم
    const tableBody = elements.bookTable;

    const tr = document.createElement('tr');

    // نام کتاب
    const tdName = document.createElement('td');
    tdName.textContent = book.title;
    tdName.className = CLASSES.NAME;

    // نویسنده (اضافه شده)
    const tdAuthor = document.createElement('td');
    tdAuthor.textContent = book.author;
    tdAuthor.className = CLASSES.TD;

    // تعداد
    const tdQuantity = document.createElement('td');
    tdQuantity.textContent = book.quantity;
    tdQuantity.className = CLASSES.TD;

    // قیمت
    const tdPrice = document.createElement('td');
    tdPrice.textContent = book.price;
    tdPrice.className = CLASSES.TD;

    // ID
    const tdId = document.createElement('td');
    tdId.textContent = book.id || '---';
    tdId.className = CLASSES.TD;

    // دکمه‌های عملیات
    const tdActions = document.createElement('td');
    tdActions.className = 'px-6 py-4 whitespace-nowrap flex';

    const editBtn = document.createElement('button');
    editBtn.innerHTML = '<img src="assets/edit.svg" alt="ویرایش">';
    editBtn.className = 'mx-4 bg-transparent border-none';

    const deleteBtn = document.createElement('button');
    deleteBtn.innerHTML = '<img src="assets/trash.svg" alt="حذف">';
    deleteBtn.className = 'bg-transparent border-none';

    tdActions.append(editBtn, deleteBtn);

    // اضافه کردن تمام سلول‌ها به ردیف
    tr.append(
        tdName,
        tdAuthor, // اضافه شده
        tdQuantity,
        tdPrice,
        tdId,
        tdActions
    );

    // اضافه کردن ردیف به جدول (فقط یک بار)
    tableBody.appendChild(tr);

    console.log('ردیف با موفقیت اضافه شد:', tr);
    console.log('محتویات جدول:', tableBody.innerHTML);
}
